package models;

public class HocVien {
    String MaHV;
    String HoHV;
    String TenHV;
    String Phai;
    String NoiSinh;
    String Facebook;
    String MaLop;
    String SoDT;
    String Email;
    String NgayDK;
    String TinhTrang;
    String GhiChu;
    String MaNV;
	public HocVien() {
		// TODO Auto-generated constructor stub
	}
	public HocVien(String MaHV, String HoHV, String TenHV, String Phai, String NoiSinh, String Facebook, String MaLop, String SoDT, String Email, String NgayDK,
    String TinhTrang, String GhiChu, String MaNV) {
		this.MaHV=MaHV;
		this.HoHV=HoHV;
		this.TenHV=TenHV;
		this.Phai=Phai;
		this.NoiSinh=NoiSinh;
		this.Facebook=Facebook;
		this.MaLop=MaLop;
		this.SoDT=SoDT;
		this.Email=Email;
		this.NgayDK=NgayDK;
		this.TinhTrang=TinhTrang;
		this.GhiChu=GhiChu;
		this.MaNV=MaNV;
		
	}
	public String getMaHV() {
		return MaHV;
	}
	public void setMaHV(String maHV) {
		MaHV = maHV;
	}
	public String getHoHV() {
		return HoHV;
	}
	public void setHoHV(String hoHV) {
		HoHV = hoHV;
	}
	public String getTenHV() {
		return TenHV;
	}
	public void setTenHV(String tenHV) {
		TenHV = tenHV;
	}
	public String getPhai() {
		return Phai;
	}
	public void setPhai(String phai) {
		Phai = phai;
	}
	public String getNoiSinh() {
		return NoiSinh;
	}
	public void setNoiSinh(String noiSinh) {
		NoiSinh = noiSinh;
	}
	public String getFacebook() {
		return Facebook;
	}
	public void setFacebook(String facebook) {
		Facebook = facebook;
	}
	public String getMaLop() {
		return MaLop;
	}
	public void setMaLop(String maLop) {
		MaLop = maLop;
	}
	public String getSoDT() {
		return SoDT;
	}
	public void setSoDT(String soDT) {
		SoDT = soDT;
	}
	public String getEmail() {
		return Email;
	}
	public void setEmail(String email) {
		Email = email;
	}
	public String getNgayDK() {
		return NgayDK;
	}
	public void setNgayDK(String ngayDK) {
		NgayDK = ngayDK;
	}
	public String getTinhTrang() {
		return TinhTrang;
	}
	public void setTinhTrang(String tinhTrang) {
		TinhTrang = tinhTrang;
	}
	public String getGhiChu() {
		return GhiChu;
	}
	public void setGhiChu(String ghiChu) {
		GhiChu = ghiChu;
	}
	public String getMaNV() {
		return MaNV;
	}
	public void setMaNV(String maNV) {
		MaNV = maNV;
	}
}
